mapper.select:
SELECT chat_history.id as id, chat_history.sender as sender, chat_history.receiver as receiver, chat_history.type as type, chat_history.file_id as fileId, chat_files.file_name as fileName, chat_files.file_type as fileType, chat_files.file_size as fileSize, chat_history.content as content, chat_history.send_time as time FROM chat_history LEFT JOIN chat_files ON chat_history.file_id = chat_files.file_id
((chat_history.owner = #{userId} AND chat_history.receiver = #{friendId}) OR (chat_history.owner = #{userId} AND chat_history.sender = #{friendId}))
AND send_time < #{lastTime}
ORDER BY send_time DESC LIMIT #{pageSize}

SELECT chat_group_history.id as id, chat_group_history.sender as sender, users.avatar as senderAvatar, users.username as senderName, chat_group_history.receiver as receiver, chat_group_history.type as type, chat_group_history.file_id as fileId, chat_group_files.file_name as fileName, chat_group_files.file_type as fileType, chat_group_files.file_size as fileSize, chat_group_history.content as content, chat_group_history.send_time as time FROM chat_group_history LEFT JOIN chat_group_files ON chat_group_history.file_id = chat_group_files.file_id LEFT JOIN users ON chat_group_history.sender = users.id
chat_group_history.receiver = #{groupId} AND chat_group_history.owner = #{userId}
AND send_time < #{lastTime}
ORDER BY send_time DESC LIMIT #{pageSize}

SELECT COUNT(*) as total
FROM chat_files
WHERE chat_files.sender = #{userId}
   OR chat_files.receiver = #{userId}

SELECT file_path
FROM chat_files
WHERE file_id = #{fileId}

SELECT file_path
FROM chat_group_files
WHERE file_id = #{fileId}

SELECT image_path
FROM chat_images
WHERE image_id = #{fileId}

SELECT image_path
FROM chat_group_images
WHERE image_id = #{fileId}
